const results = [];

for (const item of items) {
  const coin = item.json;
  const coinName = coin.name;
  const currentPrice = coin.current_price;

  // 1 SAATLİK VERİ
  if (coin.price_change_percentage_1h_in_currency !== undefined) {
    results.push({
      json: {
        coin: coinName,
        fiyat: currentPrice,
        sure: "1 Saat",
        degisim: coin.price_change_percentage_1h_in_currency,
        content: `Son 1 saat içinde fiyatta %${Number(coin.price_change_percentage_1h_in_currency).toFixed(2)} oranında bir değişim gözlemlendi. Kısa vadeli dalgalanma analizi.`
      }
    });
  }

  // 24 SAATLİK VERİ
  if (coin.price_change_percentage_24h_in_currency !== undefined) {
    results.push({
      json: {
        coin: coinName,
        fiyat: currentPrice,
        sure: "24 Saat",
        degisim: coin.price_change_percentage_24h_in_currency,
        content: `Piyasa son 24 saatte %${Number(coin.price_change_percentage_24h_in_currency).toFixed(2)} değişim gösterdi. Günlük trendin yönünü anlamak için önemli bir veri.`
      }
    });
  }

  // 7 GÜNLÜK VERİ
  if (coin.price_change_percentage_7d_in_currency !== undefined) {
    results.push({
      json: {
        coin: coinName,
        fiyat: currentPrice,
        sure: "7 Gün",
        degisim: coin.price_change_percentage_7d_in_currency,
        content: `Haftalık bazda %${Number(coin.price_change_percentage_7d_in_currency).toFixed(2)} oranında hareketlilik mevcut. Orta vadeli trend takibi için kritik.`
      }
    });
  }

  // 30 GÜNLÜK VERİ
  if (coin.price_change_percentage_30d_in_currency !== undefined) {
    results.push({
      json: {
        coin: coinName,
        fiyat: currentPrice,
        sure: "30 Gün",
        degisim: coin.price_change_percentage_30d_in_currency,
        content: `Aylık kapanışa doğru %${Number(coin.price_change_percentage_30d_in_currency).toFixed(2)} değişim var. Genel piyasa hissiyatını yansıtıyor.`
      }
    });
  }

  // 6 AYLIK (200 GÜN) VERİ - YENİ EKLENDİ!
  if (coin.price_change_percentage_200d_in_currency !== undefined) {
    results.push({
      json: {
        coin: coinName,
        fiyat: currentPrice,
        sure: "6 Ay",
        degisim: coin.price_change_percentage_200d_in_currency,
        content: `Son 6 aylık geniş periyotta %${Number(coin.price_change_percentage_200d_in_currency).toFixed(2)} oranında değişim görüldü. Uzun vade yatırımcısı için trend dönüşüm sinyali olabilir.`
      }
    });
  }

  // 1 YILLIK VERİ
  if (coin.price_change_percentage_1y_in_currency !== undefined) {
    results.push({
      json: {
        coin: coinName,
        fiyat: currentPrice,
        sure: "1 Yıl",
        degisim: coin.price_change_percentage_1y_in_currency,
        content: `Yıllık bazda %${Number(coin.price_change_percentage_1y_in_currency).toFixed(2)} değişim. Makro ekonomik döngü ve uzun vadeli yatırım performansı göstergesi.`
      }
    });
  }
}

return results;

// Önceki kutudan gelen 5 parça veriyi alıyoruz
const items = $input.all();

// Havalı borsa yorumları havuzu
const pozitifYorumlar = [
  "Boğalar piyasada! Yükseliş trendi yatırımcısını güldürüyor.",
  "Güçlü alım sinyalleri var, grafikler yeşile boyandı.",
  "Direnç noktası kırıldı, yukarı yönlü ivme devam edebilir.",
  "Yatırımcı güveni tazelendi, pozitif hava hakim."
];

const negatifYorumlar = [
  "Ayılar sahnede, satış baskısı hissediliyor.",
  "Kar realizasyonu başladı, grafikte geri çekilme var.",
  "Piyasa kararsız, düşüş trendi risk oluşturuyor.",
  "Destek seviyelerine dikkat, aşağı yönlü hareket gözleniyor."
];

const notrYorumlar = [
  "Piyasa yatay seyrediyor, fırtına öncesi sessizlik olabilir.",
  "Kararsızlık hakim, belirgin bir yön tayini bekleniyor.",
  "Hacim düşük, fiyatlar dar bir bantta sıkıştı."
];

// Her satır için mantıklı ama rastgele bir yorum seç
return items.map(item => {
  // CoinGecko'dan gelen değişim oranını al
  const degisim = item.json.degisim; 
  let yorum = "";

  // Rastgele seçim fonksiyonu
  const rastgeleSec = (liste) => liste[Math.floor(Math.random() * liste.length)];

  // Mantık: Yüzdeye bak, uygun listeden rastgele cümle seç
  if (degisim > 0.5) {
    yorum = rastgeleSec(pozitifYorumlar);
  } else if (degisim < -0.5) {
    yorum = rastgeleSec(negatifYorumlar);
  } else {
    yorum = rastgeleSec(notrYorumlar);
  }

  // Verinin içine 'content' adıyla ekle (AI yapmış gibi)
  item.json.content = yorum;

  return item;
});
